INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178188, 'Epirubicin', 'Lomitapide', 'Moderate', 'Coadministration with lomitapide may increase the plasma concentrations of drugs that are substrates of P-glycoprotein. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of P-glycoprotein-mediated drug efflux by lomitapide.', 'DDInter', 'Caution is advised if lomitapide must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lomitapide is added to or withdrawn from therapy.', 'Absorption', 'Caution is advised if lomitapide must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178399/', '', 'Valrubicin, Plicamycin, Ixabepilone, Bleomycin, Mitomycin, Dactinomycin', 'Gemfibrozil, Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Dextrothyroxine, Lovastatin, Cholestyramine, Clofibrate, Colestipol, More', 1767369485);
