INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178338, 'Epoprostenol', 'Eptifibatide', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178549/', '', 'Protein C, Acenocoumarol, Selexipag, Epoprostenol, Treprostinil', 'Eptifibatide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178339, 'Ertugliflozin', 'Epoprostenol', 'Moderate', 'Sodium-glucose co-transporter 2 (SGLT-2) inhibitors may potentiate the hypotensive effects of diuretics and other antihypertensive agents or vasodilators. Inhibition of glucose and sodium co-transport produces mild diuresis and transient natriuresis, resulting in intravascular volume contraction. Volume depletion-related adverse reactions including hypotension, postural dizziness, orthostatic hypotension, syncope, and dehydration can occur after initiating treatment with SGLT-2 inhibitors, and the risk may be increased with concomitant use of other agents that can lower blood pressure.', 'DDInter', 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function. Prior to initiating SGLT-2 inhibitors, volume status should be assessed and corrected, if necessary. Clinical and laboratory monitoring are recommended during therapy, including electrolytes, fluid status, renal function, and blood pressure. If volume depletion occurs, treatment with SGLT-2 inhibitors should be interrupted until the condition is corrected.', 'Synergism', 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178550/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, More', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Dulaglutide, Exenatide, To