INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178475, 'Eptifibatide', 'Dexfenfluramine', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178686/', '', 'Dicoumarol, Protein C, Acenocoumarol, Selexipag, Anistreplase', 'Naltrexone, Ephedrine, Bupropion, Phentermine, Orlistat, Lorcaserin, Diethylpropion, Mazindol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178476, 'Eptifibatide', 'Dicoumarol', 'Major', 'The risk of bleeding, including major bleeding (intracranial hemorrhage or a decrease in hemoglobin greater than 5 mg/dl) is significantly more likely when oral anticoagulants and glycoprotein IIb/IIIa inhibitors are given together.', 'DDInter', 'Some manufacturers of GP IIb/IIIa inhibitors consider their use contraindicated if patients have received oral anticoagulants within 7 days unless the prothrombin time is less than or equal to 1.2 times control. Bleeding precautions should be observed and patients should be closely monitored for clinical and laboratory evidence of adverse effects.', 'Synergism', 'Some manufacturers of GP IIb/IIIa inhibitors consider their use contraindicated if patients have received oral anticoagulants within 7 days unless the prothrombin time is less than or equal to 1.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178687/', '', 'Warfarin, Cilostazol, Dicoumarol, Antithrombin III human, Acenocoumarol, Selexipag, Anistreplase', 'Eptifibatide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag', 1767369485);
