INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178492, 'Heparin', 'Eptifibatide', 'Major', 'Although glycoprotein IIb/IIIa inhibitors are routinely given with heparin, be cognizant of the increased risk of bleeding due to additive pharmacologic effects. Data have shown increased incidences of major bleeding complications, including hemorrhagic stroke, retroperitoneal bleeding, spontaneous gastrointestinal (GI) and genitourinary (GU) bleeding among patients receiving both agents compared with the risk associated with the use of either agent alone. Arterial puncture sites may be most problematic.', 'DDInter', 'Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Glycoprotein inhibitor/heparin therapy should be discontinued immediately if serious or uncontrollable bleeding occurs or if thrombocytopenia is confirmed. Low-dose, weight-adjusted heparin is recommended in patients who are receiving abciximab and undergoing percutaneous coronary intervention. Heparin should be discontinued for 3 to 4 hours before removing the sheath.', 'Synergism', 'Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178703/', '', 'Selexipag, Heparin', 'Eptifibatide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Ascorbic acid, Cyclosporine, Sirolimus, Potassium Iodide, Vitamin A, Nandrolone', 1767369485);
