INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178503, 'Eptifibatide', 'Sibutramine', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178714/', '', 'Protein C, Acenocoumarol, Selexipag', 'Naltrexone, Ephedrine, Bupropion, Phentermine, Orlistat, Lorcaserin, Diethylpropion, Mazindol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178504, 'Eptifibatide', 'Salicylic acid (sodium)', 'Moderate', 'Although aspirin is routinely given with glycoprotein IIb/IIIa inhibitors, be cognizant of the increased risk of bleeding associated with this combination due to the additive inhibitory effects of salicylates on platelet function.', 'DDInter', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Therapy should be discontinued immediately if serious or uncontrollable bleeding or thrombocytopenia occurs.', 'Synergism', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178715/', '', 'Cilostazol, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Streptokinase, Tinzaparin, Selexipag, Vorapaxar, Dicoumarol, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178505, 'Eptifibatide', 'Streptokinase', 'Major', 'The intended and unintended effects of glycoprotein IIb/IIIa inhibitors and thrombolytic agents are additive, such that there is an increased frequency of major bleeding complications, including intracranial hemorrhage, retroperitoneal bleeding, spontaneous gastrointestinal (GI) and genitourinary (GU) bleeding among patients receiving both agents compared with the risk associated with the use of either agent alone.', 'DDInter', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications. The successful use of combination therapy with glycoprotein IIb/IIIa inhibitors and reduced doses of thrombolytics has been reported in clinical trials. Patients should be closely monitored for signs of bleeding, especially at arterial puncture sites. Thrombolytic and/or glycoprotein inhibitor therapy should be discontinued immediately if serious or uncontrollable bleeding occurs.', 'Synergism', 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178716/', '', 'Alteplase, Streptokinase, Antithrombin III human, Tenecteplase, Acenocoumarol, Selexipag, Urokinase, Anistreplase, Reteplase', 'Eptifibatide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Human C1-esterase inhibitor, Berotralstat, More', 1767369485);
