INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178786, 'Eribulin', 'Typhoid vaccine (live)', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178997/', '', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, Ivosidenib, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178787, 'Eribulin', 'Vinblastine', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect. Patient risk factors include diabetes and age older than 60 years.', 'DDInter', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet. Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded. Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.', 'Synergism', 'Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/178998/', '', 'Irinotecan', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Pegaspargase, Enasidenib, Mitotane, Aflibercept, Trastuzumab, Anagrelide, More', 1767369485);
