INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178987, 'Escitalopram', 'Cyproheptadine', 'Moderate', 'Serotonin antagonists such as cyproheptadine and methysergide may diminish the pharmacologic effects of selective serotonin reuptake inhibitors (SSRIs) and other highly serotonergic agents (e.g., 5-hydroxytryptophan; buspirone; mirtazapine; nefazodone; sibutramine; St. John''s wort; trazodone; SNRIs such as duloxetine, desvenlafaxine, milnacipran, and venlafaxine). This pharmacodynamic interaction is sometimes exploited in the use of cyproheptadine and methysergide for the treatment of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Theoretically, serotonergic agents may also antagonize the pharmacologic effects of cyproheptadine and methysergide, although this effect has not been reported.', 'DDInter', 'Patients should be monitored for potentially diminished therapeutic response to serotonergic agents during coadministration with cyproheptadine or methysergide, and dosages adjusted as necessary.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179198/', '', 'Ketotifen, Fexofenadine, Dexchlorpheniramine, Loratadine, Desloratadine, Azelastine', 'Bupropion, Viloxazine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (178988, 'Escitalopram', 'Danaparoid', 'Moderate', 'Serotonin reuptake inhibitors may potentiate the risk of bleeding in patients receiving heparin therapy. The exact mechanism is unknown but may involve a pharmacodynamic interaction, as serotonin reuptake inhibitors have been reported to interfere with platelet function.', 'DDInter', 'Caution is recommended if a serotonin reuptake inhibitor is administered with heparin therapy, especially in the elderly and patients with renal impairment. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if a serotonin reuptake inhibitor is administered with heparin therapy, especially in the elderly and patients with renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179199/', '', 'Maprotiline, St. John''s Wort, Doxepin, Isocarboxazid, Viloxazine, Amoxapine, Imipramine, Esketamine, Phenelzine, Desipramine, Tranylcypromine, More', 'Protein C, Acenocoumarol, Selexipag', 1767369485);
