INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (179393, 'Guanfacine', 'Esmolol', 'Moderate', 'Use of central alpha-2 adrenergic agonists, such as clonidine or guanfacine, with beta-blockers may have synergistic pharmacodynamic effects resulting in AV block, bradycardia, and hypotension. In addition, sinus node dysfunction may also be enhanced. Abrupt withdrawal of an alpha-2 agonist or both the alpha-2 agonist and the beta-blocker may also result in rebound hypertensive effects. The proposed mechanism is related to increased catecholamine release after alpha-2 agonist withdrawal which, when combined with concurrent beta-blockade, results in unopposed vasoconstriction.', 'DDInter', 'Until more information is available, caution and close monitoring of blood pressure are recommended for patients receiving guanfacine with a beta-blocker. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some patients. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, headaches, or reduced or irregular heart rate.', 'Synergism', 'Until more information is available, caution and close monitoring of blood pressure are recommended for patients receiving guanfacine with a beta-blocker.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179606/', '', 'Methyldopa, Deserpidine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (179394, 'Halazepam', 'Esmolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179607/', '', 'Timolol, Carteolol', '', 1767369485);
