INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (179700, 'Dienogest', 'Pitolisant', 'Moderate', 'RECOMMENDED: Coadministration with pitolisant may decrease the plasma concentrations of contraceptive hormones. The proposed mechanism is induction of the CYP450 3A4-mediated metabolism of estrogens and progestins by pitolisant and its main metabolites.', 'DDInter', 'The manufacturer recommends that alternative, nonhormonal methods of effective contraception should be used during and for at least 21 days after pitolisant therapy.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/179913/', '', 'Megestrol acetate, Estetrol, Estradiol, Medroxyprogesterone acetate, Megestrol acetate, Progesterone, Medroxyprogesterone acetate, Megestrol acetate, Progesterone, Estradiol, Medroxyprogesterone acetate', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Amifampridine, Deutetrabenazine, Tafamidis', 1767369485);
