INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (179927, 'Aminoglutethimide', 'Estrone sulfate', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180140/', '', 'Darolutamide, Tamoxifen, Fulvestrant, Toremifene, Abarelix, Flutamide, Relugolix, Bicalutamide, Degarelix, Nilutamide, Abiraterone', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (179928, 'Amoxapine', 'Estrone sulfate', 'Minor', 'The effects of tricyclic antidepressants (TCAs) may be altered in women receiving estrogen-containing therapy. Simultaneous TCA toxicity and reduced effects have been reported. Akathisia has also been reported in some women taking this combination. The mechanism of interaction is unknown but may be related to increased TCA bioavailability or inhibition of hepatic TCA metabolism.', 'DDInter', 'The clinical significance of this interaction has not been established. Monitoring for altered effects may be advisable during concomitant therapy. Dose adjustments of the TCA may be required if an interaction is suspected.', 'Others', 'The clinical significance of this interaction has not been established.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180141/', '', 'Citalopram, Sertraline, Maprotiline, Fluoxetine, Milnacipran, Vortioxetine, Levomilnacipran, Duloxetine, Isocarboxazid, Viloxazine, Esketamine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (179929, 'Anisindione', 'Estrone sulfate', 'Moderate', 'Concomitant therapy with estrogen-containing drugs may diminish the therapeutic effects of oral anticoagulants. Estrogens can increase the plasma levels of certain clotting factors such as fibrinogen, prothrombin, and factors VII and VIII in a dose-dependent manner, resulting in increased risk of thromboembolism, stroke, and/or myocardial infarction. The risk may be further increased by lifestyle choices such as smoking and lack of exercise.', 'DDInter', 'Use of estrogen-containing drugs should be avoided in patients receiving anticoagulant therapy unless benefits are anticipated to outweigh the risks. Close clinical and laboratory monitoring are recommended if the combination is prescribed. Patients should be advised to promptly notify their physician if they experience potential signs and symptoms of blood clots such as chest pain, shortness of breath, sudden loss of vision, and pain, redness or swelling in an extremity.', 'Antagonism', 'Use of estrogen-containing drugs should be avoided in patients receiving anticoagulant therapy unless benefits are anticipated to outweigh the risks.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180142/', '', '', '', 1767369485);
