INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (180224, 'Furosemide', 'Etacrynic acid', 'Major', 'Coadministration of furosemide and ethacrynic acid may increase the risk of ototoxicity. Cases of tinnitus, reversible or irreversible hearing impairment, and deafness have been reported. Additional risk factors for ototoxicity include rapid injection, severe renal impairment, the use of higher than recommended dosages, and hypoproteinemia.', 'DDInter', 'Concomitant use of furosemide and ethacrynic acid should be avoided.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180437/', '', 'Bumetanide, Torasemide, Etacrynic acid, Bumetanide, Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, More', 'Furosemide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (180225, 'Garlic', 'Etacrynic acid', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180438/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (180226, 'Gentamicin', 'Etacrynic acid', 'Major', 'Coadministration of parenteral or inhaled aminoglycoside antibiotics or oral neomycin in combination with loop diuretics may potentiate the risk of oto- or nephrotoxicity due to additive or synergistic pharmacologic effects of these drugs and or altered aminoglycoside serum and tissue levels. Coadministration of aminoglycosides with intravenous mannitol may increase the risk of nephrotoxicity. The risk may be greater with high dosages of either drug, preexisting renal insufficiency, advanced age, dehydration, or the presence of other oto- or nephrotoxic drugs. The onset of ototoxicity may be greatly delayed, and cochlear damage may initially be asymptomatic.', 'DDInter', 'The use of aminoglycoside antibiotics in combination with loop diuretics or intravenous mannitol should generally be avoided. Renal function tests and serial, vestibular, and audiometric tests should be performed as appropriate for the type of diuretic before and during therapy if coadministration is necessary.', 'Synergism', 'The use of aminoglycoside antibiotics in combination with loop diuretics or intravenous mannitol should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180439/', '', 'Gatifloxacin, Acyclovir, Rifamycin, Ganciclovir, Famciclovir, Sulfamethizole, Trifluridine, Moxifloxacin, Ampicillin, Benzylpenicillin, Chloramphenicol, More', '', 1767369485);
