INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (180492, 'Fluorouracil', 'Ethotoin', 'Moderate', 'Concomitant administration with levamisole and 5-fluorouracil (5-FU) has been reported to increase serum levels of phenytoin. No mechanism has been established for this interaction, and it is unknown whether levamisole or 5-FU, or both, is responsible.', 'DDInter', 'Pharmacologic response to phenytoin should be monitored more closely whenever levamisole, 5-FU, or capecitabine (which is metabolized to 5-FU in vivo) is added to or withdrawn from therapy, and the phenytoin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience symptoms of phenytoin toxicity, including drowsiness, visual disturbances, mental status changes, seizures, nausea, or ataxia. It is uncertain whether this interaction may also occur with other hydantoins.', 'Others', 'Pharmacologic response to phenytoin should be monitored more closely whenever levamisole, 5-FU, or capecitabine (which is metabolized to 5-FU in vivo) is added to or withdrawn from therapy, and the phenytoin dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180705/', '', 'Pemetrexed, Mercaptopurine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Cytarabine, Pralatrexate, Fludarabine, Decitabine, Azacitidine', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 1767369485);
