INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (180676, 'Etodolac', 'Probenecid', 'Minor', 'Probenecid may interfere with the plasma protein binding, metabolism, and/or renal elimination of nonsteroidal anti-inflammatory drugs (NSAIDs), resulting in increased NSAID plasma levels. The risk of NSAID toxicity may be increased. Although adverse effects from this interaction have not been reported, patients receiving this combination should be monitored for increased NSAID side effects. Patients should be advised to report possible signs of NSAID toxicity such as dizziness, drowsiness, headache, tinnitus, nausea, vomiting, dyspepsia, abdominal pain, diarrhea, or black tarry stools.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/180890/', '', 'Phenylbutazone, Misoprostol, Glucosamine, Chondroitin sulfate, Esomeprazole, Valdecoxib', 'Lesinurad, Febuxostat, Pegloticase, Colchicine, Rasburicase, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Sulfadiazine, Lactic acid, More', 1767369485);
