INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (180948, 'Amprenavir', 'Everolimus', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia may be increased.', 'DDInter', 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided. Some authorities recommend avoiding concomitant use of everolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of everolimus with potent inhibitors of CYP450 3A4 and P-glycoprotein should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181162/', '', 'Lamivudine, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 'Futibatinib, Tucatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib, More', 1767369485);
