INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (181757, 'Glycerol phenylbutyrate', 'Fenfluramine', 'Moderate', 'Coadministration of glycerol phenylbutyrate may lead to increased concentrations of CYP450 2D6 substrates. The proposed mechanism is inhibition of CYP450 2D6 by phenylbutyrate, the active moiety of glycerol phenylbutyrate, which has been shown to be an in vitro inhibitor of this isoenzyme.', 'DDInter', 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate.', 'Metabolism', 'Caution is recommended if glycerol phenylbutyrate is administered with medicines that are substrates of CYP450 2D6, such as codeine, dextromethorphan, and some beta blockers (including metoprolol, carvedilol, labetalol, propranolol, and nebivolol).', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181971/', '', 'Naltrexone, Ephedrine, Bupropion, Phentermine, Lorcaserin, Sibutramine, Diethylpropion, Mazindol, Brivaracetam, Ezogabine, Cenobamate, More', 'Teduglutide, Lonafarnib, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (181758, 'Granisetron', 'Fenfluramine', 'Major', 'Concomitant use of 5-HT3 receptor antagonists with agents that possess or enhance serotonergic activity such as selective serotonin reuptake inhibitors (SSRIs), selective serotonin-norepinephrine reuptake inhibitors (SNRIs), monoamine oxidase inhibitors (MAOIs), tricyclic antidepressants (TCAs), 5-HT1 receptor agonists (triptans), ergot alkaloids, phenylpiperidine opioids, bupropion, dextromethorphan, linezolid, lithium, St. John''s wort, tramadol, and tryptophan may potentiate the risk of serotonin syndrome.', 'DDInter', 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is warranted when initiating or increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate a prolonged elimination half-life. For example, a 5-week washout period is typically recommended following use of fluoxetine and 2 weeks following use of MAOIs before administering another serotonergic agent. If serotonin syndrome develops or is suspected during the course of therapy, all serotonergic agents should be discontinued immediately and supportive care rendered as necessary.', 'Synergism', 'Caution is advised if 5-HT3 receptor antagonists are prescribed with other agents that affect the serotonergic neurotransmitter system.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181972/', '', 'Naltrexone, Ephedrine, Phentermine, Orlistat, Lorcaserin, Diethylpropion, Mazindol, Brivaracetam, Topiramate, Lamotrigine, Methylphenobarbital, More', 'Aprepitant', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (181759, 'Heparin', 'Fenfluramine', 'Moderate', 'Serotonin reuptake inhibitors may potentiate the risk of bleeding in patients receiving heparin therapy. The exact mechanism is unknown but may involve a pharmacodynamic interaction, as serotonin reuptake inhibitors have been reported to interfere with platelet function.', 'DDInter', 'Caution is recommended if a serotonin reuptake inhibitor is administered with heparin therapy, especially in the elderly and patients with renal impairment. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if a serotonin reuptake inhibitor is administered with heparin therapy, especially in the elderly and patients with renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181973/', '', 'Naltrexone, Ephedrine, Phentermine, Lorcaserin, Diethylpropion, Mazindol, Brivaracetam, Ezogabine, Cenobamate, Methylphenobarbital, Ethosuximide, More', 'Protein C, Acenocoumarol, Selexipag, Pentosan polysulfate, Ascorbic acid, Cyclosporine, Sirolimus, Potassium Iodide, Vitamin A, Acetylcysteine, Nandrolone', 1767369485);
