INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (181774, 'Fenfluramine', 'Tositumomab (I-131)', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', 'DDInter', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181988/', '', 'Naltrexone, Ephedrine, Bupropion, Phentermine, Orlistat, Lorcaserin, Diethylpropion, Mazindol, Brivaracetam, Topiramate, Ezogabine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (181775, 'Isoetharine', 'Fenfluramine', 'Moderate', 'Coadministration of beta-2 adrenergic agonists with other adrenergic agents may potentiate the risk of cardiovascular side effects. Beta-2 adrenergic agonists can produce clinically significant cardiovascular effects including increases in pulse rate and systolic or diastolic blood pressure as well as ECG changes such as flattening of the T wave, prolongation of the QTc interval, and ST segment depression. The risk is lower when beta-2 adrenergic agonists are inhaled at normally recommended dosages.', 'DDInter', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension. Blood pressure and heart rate should be closely monitored.', 'Synergism', 'Caution is advised if beta-2 adrenergic agonists are used concomitantly with other adrenergic agents, particularly in patients with cardiovascular disorders such as coronary insufficiency, cardiac arrhythmias, hypertrophic obstructive cardiomyopathy, or hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181989/', '', 'Dexfenfluramine, Naltrexone, Orlistat, Lorcaserin, Mazindol, Brivaracetam, Topiramate, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (181776, 'Fenfluramine', 'Ketamine', 'Moderate', 'Ketamine can cause hemodynamic instability such as arrhythmias and transient increases or decreases in blood pressure, heart rate, and cardiac index. Coadministration of ketamine with direct or indirect acting sympathomimetics or vasopressin may enhance the sympathomimetic effects of ketamine leading to additive increases in blood pressure and heart rate.', 'DDInter', 'Close monitoring of vital signs, including blood pressure and heart rate is recommended whenever ketamine is used concomitantly with direct or indirect acting sympathomimetics or vasopressin.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/181990/', '', 'Dexfenfluramine, Naltrexone, Bupropion, Orlistat, Lorcaserin', 'Halothane, Isoflurane, Nitrous oxide, Desflurane, Propofol, Enflurane, Sevoflurane, Etomidate', 1767369485);
