INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (181795, 'Fenfluramine', 'Thioridazine', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may increase the plasma concentrations of thioridazine and its two active metabolites, mesoridazine and sulforidazine, all of which are substrates of the isoenzyme.', 'DDInter', 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated. Depending on the elimination half-life of these drugs, a considerable waiting period may be appropriate following their discontinuation before thioridazine is initiated. For example, the manufacturer of fluoxetine recommends that thioridazine not be administered within 5 weeks after discontinuing fluoxetine because of the drug''s long half-life.', 'Metabolism', 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182009/', '', 'Orlistat, Naltrexone, Lacosamide', 'Amisulpride, Pimavanserin', 1767369485);
