INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (181851, 'Fenoldopam', 'Levacetylmethadol', 'Major', 'Levomethadyl acetate can cause QT interval prolongation and torsades de pointes. Coadministration with agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in an elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes.', 'DDInter', 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be evaluated and any abnormalities corrected prior to initiating therapy with levomethadyl acetate. A 12-lead ECG should be performed before initiating therapy, 12 to 14 days after initiating therapy, and periodically thereafter. Patients should be advised to immediately seek medical attention if they experience palpitations, dizziness, lightheadedness, fainting, or seizures.', 'Synergism', 'Caution is advised if levomethadyl acetate must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182065/', '', 'Dobutamine, Norepinephrine, Midodrine, Amrinone, Droxidopa, Angiotensin II, Ephedrine, Milrinone, Arbutamine, Phenylephrine, Metaraminol, More', 'Acamprosate, Naltrexone, Varenicline, Disulfiram, Methadone, Nalmefene, Nicotine, Buprenorphine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (181852, 'Fenoldopam', 'Licorice', 'Moderate', 'Chronic use of licorice may potentiate the hypokalemic effects of some diuretics and other drugs that deplete potassium (e.g., amphotericin B, cation exchange resins). Glycyrrhizic acid, a component of licorice, possesses mineralocorticoid activity and can induce hypokalemia. Severe hypokalemia can lead to muscle paralysis, rhabdomyolysis, metabolic alkalosis, cardiac arrhythmias, and respiratory arrest.', 'DDInter', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In general, chronic use of licorice and licorice-containing products should be avoided in patients treated with potassium-depleting drugs. During concomitant use, patients should be advised to contact their physician if they experience signs and symptoms of hypokalemia such as fatigue, myalgia, muscle weakness, abdominal pain, hypoventilation, and irregular heartbeat.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182066/', '', 'Dobutamine, Norepinephrine, Midodrine, Amrinone, Droxidopa, Angiotensin II, Ephedrine, Milrinone, Arbutamine, Phenylephrine, Isoprenaline, More', '', 1767369485);
