INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182279, 'Fingolimod', 'Ritodrine', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182493/', '', 'Bremelanotide, Flibanserin, Cabergoline, Bromocriptine, Naproxen, Ibuprofen', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182280, 'Fingolimod', 'Rubella virus vaccine', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during fingolimod therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Fingolimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 'The use of live attenuated vaccines should be avoided during and for two months after treatment with fingolimod. In patients who have recently been vaccinated, fingolimod therapy should be postponed for one month to allow the full effect of vaccination to occur.', 'Others', 'The use of live attenuated vaccines should be avoided during and for two months after treatment with fingolimod.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182494/', '', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', '', 1767369485);
