INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182383, 'Flecainide', 'Astemizole', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182597/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Doxylamine, More', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182384, 'Flecainide', 'Bedaquiline', 'Major', 'Bedaquiline can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration.', 'Synergism', 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182598/', '', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', 'Capreomycin, Pyrazinamide, Rifamycin, Rifapentine, Streptomycin, Trimethoprim, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182385, 'Flecainide', 'Bepridil', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182599/', '', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182386, 'Flecainide', 'Berotralstat', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6. The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', 'DDInter', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182600/', '', 'Lidocaine, Tocainide, Procainamide, Bretylium, Moricizine, Ibutilide', 'Hemin, Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182387, 'Flecainide', 'Boceprevir', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase the plasma concentrations of certain antiarrhythmic agents including flecainide, lidocaine, and propafenone. The mechanism of interaction has not been described. The interaction has not been specifically studied, but could conceivably lead to serious and/or life-threatening reactions including cardiac arrhythmias and other toxicities if levels are significantly increased.', 'DDInter', 'Caution is advised if boceprevir or telaprevir must be used with flecainide, lidocaine, or propafenone. Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever the HCV NS3/4A protease inhibitor is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary.', 'Others', 'Caution is advised if boceprevir or telaprevir must be used with flecainide, lidocaine, or propafenone.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182601/', '', 'Tocainide, Procainamide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Lamivudine, Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, Dolutegravir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182388, 'Flecainide', 'Carteolol', 'Moderate', 'Beta-blockers and flecainide may have additive negative inotropic effects. Areas under the curve were increased for both drugs and negative inotropic effects occurred when flecainide and propranolol were given to normal subjects. A case of bradycardia, atrioventricular block and cardiac arrest has been reported after sotalol was added to flecainide.', 'DDInter', 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Patients should be advised to promptly report symptoms such as dizziness, slow or irregular heartbeats, syncope, or palpitations.', 'Synergism', 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/182602/', '', 'Apraclonidine, Methazolamide, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Diclofenamide, Bimatoprost, Travoprost, Acetazolamide, Brinzolamide, More', 'Propafenone, Procainamide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 1767369485);
