INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182850, 'Fluconazole', 'Terfenadine', 'Major', 'Coadministration with azole antifungal agents may significantly increase the plasma concentrations of terfenadine. The mechanism is azole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of terfenadine. High plasma levels of terfenadine have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia and torsade de pointes; cardiac arrest; and sudden death. Within the azole class, ketoconazole and itraconazole are considered the most potent inhibitors, while fluconazole is comparatively weak and generally causes clinically significant interactions with CYP450 3A4 substrates only at dosages of 200 mg/day or more.', 'DDInter', 'The use of terfenadine with most azole antifungal agents, including fluconazole at multiple doses of 400 mg or higher, is considered contraindicated. Some authorities consider concomitant administration of terfenadine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole (AU). Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with azole antifungal agents.', 'Metabolism', 'The use of terfenadine with most azole antifungal agents, including fluconazole at multiple doses of 400 mg or higher, is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183064/', '', 'Tinidazole, Tetracycline, Cefepime, Cefuroxime, Secnidazole, Cefixime, Amikacin, Caspofungin, Micafungin, Amphotericin B, Oteseconazole, More', 'Mepyramine, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Doxylamine, Meclizine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182851, 'Fluconazole', 'Testosterone (topical)', 'Moderate', 'Coadministration with fluconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4-mediated metabolism by fluconazole, a moderate inhibitor of the isoenzyme.', 'DDInter', 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183065/', '', 'Tinidazole, Tetracycline, Cefepime, Norfloxacin, Ciprofloxacin, Metronidazole, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, More', 'Methyltestosterone, Fluoxymesterone, Prasterone, Methyltestosterone', 1767369485);
