INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (182896, 'Fludarabine', 'Cytarabine', 'Moderate', 'INTERVAL: Cytarabine can impair the metabolism of fludarabine. The mechanism is inhibition of phosphorylation of arabinosyl-2-fluoroadenine. As a result, plasma levels of the active fludarabine triphosphate, F-ara-ATP, are decreased. Also, F-ara-ATP can potentiate deoxycytidine kinase--that is, the phosphorylation of cytarabine. As a result, plasma levels of the active cytarabine triphosphate, ara-CTP, are increased.', 'DDInter', 'It has been suggested that cytarabine be given as a relatively short-term infusion (two to four hours) following fludarabine to minimize metabolic interference with the subsequent fludarabine dose and to maximize ara-CTP synthesis.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183110/', '', 'Pemetrexed, Clofarabine, Trifluridine, Pralatrexate, Fludarabine, Decitabine, Azacitidine, Capecitabine, Pertuzumab, Rucaparib, Tisagenlecleucel, More', 'Azacitidine, Pralatrexate, Cytarabine, Trifluridine', 1767369485);
