INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (183056, 'Fludrocortisone', 'Pipecuronium', 'Moderate', 'Corticosteroids may inhibit or enhance the action of nondepolarizing skeletal muscle relaxants. The mechanism is unknown. In addition, the risk of myopathy may be increased after long-term use of neuromuscular blocking agents and corticosteroids.', 'DDInter', 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy. Dose adjustments of the muscle relaxant may be required. The benefit-to-risk ratio should be considered and the duration of administration of the neuromuscular blocking agent should be limited as much as clinically feasible.', 'Others', 'Patients should be closely monitored for altered neuromuscular blockade and signs of myopathy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183270/', '', 'Fluocinolone acetonide, Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac', 'Botulinum toxin type A', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (183057, 'Fludrocortisone', 'Polythiazide', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', 'DDInter', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183271/', '', 'Fluocinolone acetonide, Fluocinolone acetonide, Fluorometholone', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Clindamycin, Ciclopirox, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (183058, 'Fludrocortisone', 'Promazine', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', 'DDInter', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183272/', '', 'Thiothixene, Quetiapine, Prochlorperazine, Lumateperone, Cariprazine, Lurasidone, Mesoridazine, Methotrimeprazine, Pimavanserin, Clozapine, Triflupromazine, More', 'Triamcinolone, Fluocinolone acetonide, Prednisolone, Dexamethasone, Indomethacin, Fluocinolone acetonide, Diclofenac, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (183059, 'Fludrocortisone', 'Quinestrol', 'Moderate', 'Estrogens may enhance the systemic effects of both endogenous and exogenous corticosteroids. The proposed mechanism is an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in a decreased rate of corticosteroid metabolic clearance. The interaction has been reported with estrogens or estrogen-containing oral contraceptives (OCs) and hydrocortisone, prednisone, and prednisolone.', 'DDInter', 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever an estrogen is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Synergism', 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183273/', '', 'Fluocinolone acetonide, Fluorometholone, Indomethacin, Fluocinolone acetonide, Diclofenac', '', 1767369485);
