INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (183397, 'Fluphenazine', 'Phenobarbital', 'Moderate', 'Concomitant use of phenothiazines with barbiturates may reduce the plasma concentrations and therapeutic effects of both agents. The mechanism is unclear but may relate to metabolic induction of CYP450 hepatic enzymes. In addition, coadministration of phenothiazines and barbiturates may lead to a reduction in seizure threshold and additive central nervous system (CNS) and respiratory depressant effects.', 'DDInter', 'Administration of phenothiazines in patients receiving large doses of barbiturates is considered contraindicated by some authorities (US). If coadministration is required, caution as well as clinical and laboratory monitoring should be considered whenever either of these agents is added to or withdrawn from therapy. If chlorpromazine is coadministered with a barbiturate, the manufacturer of chlorpromazine recommends using 1/4 to 1/2 of the usual barbiturate dosage. Patients should be monitored for hypotension, loss of seizure control and the development of adverse effects including CNS and respiratory depression.', 'Others', 'Administration of phenothiazines in patients receiving large doses of barbiturates is considered contraindicated by some authorities (US).', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183611/', '', 'Amisulpride', 'Ethchlorvynol, Zopiclone, Nitrazepam, Lacosamide', 1767369485);
