INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (183422, 'Fluphenazine', 'Thioridazine', 'Major', 'Coadministration of thioridazine with another phenothiazine or a tricyclic antidepressant (TCA) may result in elevated plasma concentrations of both drugs. Most phenothiazines and TCAs have been found to undergo metabolism by CYP450 2D6, thus competitive inhibition of the enzyme may occur when more than one of these agents are administered. In addition, these agents individually can cause prolongation of the QT interval, thus concomitant administration may result in elevated risk of ventricular arrhythmias including ventricular tachycardia and torsade de pointes.', 'DDInter', 'The use of thioridazine with drugs that can inhibit CYP450 2D6 and/or prolong the QT interval is considered contraindicated.', 'Metabolism, Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/183636/', '', 'Thioridazine', 'Fluphenazine', 1767369485);
