INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184092, 'Foscarnet', 'Relugolix', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184307/', '', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Cabotegravir, Valaciclovir, Zanamivir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Norethisterone, Nafarelin, Octreotide, Elagolix, Cetrorelix, Estradiol, Ganirelix, Lanreotide, Gonadorelin, Anastrozole, Fulvestrant, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184093, 'Foscarnet', 'Ritodrine', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184308/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Bremelanotide, Flibanserin, Cabergoline, Black cohosh, Bromocriptine, Naproxen, Ibuprofen', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184094, 'Foscarnet', 'Sparfloxacin', 'Moderate', 'Limited data suggest that coadministration of foscarnet and ciprofloxacin may potentiate the risk of seizures. Both agents are individually epileptogenic and may have additive effects when combined. Specifically, foscarnet can decrease the seizure threshold by chelating ionized calcium and magnesium, while ciprofloxacin inhibits gamma-aminobutyric acid (GABA).', 'DDInter', 'Caution is advised during coadministration of foscarnet and ciprofloxacin, particularly in elderly patients and those with renal impairment, electrolyte and metabolic abnormalities, or underlying neurologic disorders. The same precaution may be applicable during therapy with other quinolones, although clinical data are lacking.', 'Synergism', 'Caution is advised during coadministration of foscarnet and ciprofloxacin, particularly in elderly patients and those with renal impairment, electrolyte and metabolic abnormalities, or underlying neurologic disorders.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184309/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184095, 'Foscarnet', 'Streptomycin', 'Major', 'The potential nephrotoxicity of foscarnet and aminoglycosides may be additive. Concomitant use of these drugs could also lead to additive hypokalemia, hypomagnesemia, and hypocalcemia, which may increase the foscarnet-related risks of QT prolongation and seizures.', 'DDInter', 'The manufacturer recommends avoiding concomitant therapy unless the potential benefits outweigh the risks. Electrolyte levels and renal function of patients receiving these medications concomitantly should be monitored carefully, and dosages adjusted or discontinued if necessary.', 'Synergism', 'The manufacturer recommends avoiding concomitant therapy unless the potential benefits outweigh the risks.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184310/', '', 'Paritaprevir, Glecaprevir, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, Rilpivirine, More', 'Capreomycin, Pyrazinamide, Rifamycin, Rifapentine, Trimethoprim, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Pyridoxine, Ethionamide, More', 1767369485);
