INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184298, 'Fosphenytoin', 'Relugolix', 'Major', 'Coadministration with potent inducers of CYP450 3A4 that also induce P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of relugolix. In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8. Relugolix is also a substrate for intestinal P-gp.', 'DDInter', 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided. If coadministration is required, the manufacturer recommends increasing the relugolix dosage to 240 mg once daily. Therapeutic response and tolerance should be monitored more frequently. Following discontinuation of the P-gp/CYP450 3A inducer, treatment with relugolix should be resumed at the normally recommended dosage of 120 mg once daily.', 'Metabolism', 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184515/', '', 'Brivaracetam, Topiramate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Perampanel, More', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Ganirelix, Lanreotide, Gonadorelin, Anastrozole, Fulvestrant, Letrozole, Abarelix, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184299, 'Fosphenytoin', 'Riociguat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme.', 'DDInter', 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers. Patients should be closely monitored, and the dosage of riociguat adjusted as necessary. Data are not available to guide dosing of riociguat when coadministered with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Alternative agents with no or minimal CYP450 3A4-inducing activity should be considered whenever possible.', 'Metabolism', 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184516/', '', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, More', 'Ambrisentan', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184300, 'Fosphenytoin', 'Sulfadiazine', 'Moderate', 'Some sulfonamides may inhibit the hepatic metabolism of hydantoins. Serum hydantoin levels and risk of toxicity may be increased. Data are available for sulfadiazine, sulfaphenazole, and sulfamethizole.', 'DDInter', 'Monitoring for clinical and laboratory evidence of altered efficacy and safety is recommended. Hydantoin dosage adjustments or an alternate antimicrobial may be required if an interaction is suspected. Patients should be advised to notify their physician if they experience symptoms of hydantoin toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia).', 'Others', 'Monitoring for clinical and laboratory evidence of altered efficacy and safety is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184517/', '', 'Tamsulosin, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Rosuvastatin, Lactic acid, Methazolamide, Amphotericin B, Trichlormethiazide, Ciclopirox, Probenecid, More', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Tiagabine, Oxcarbazepine, Rufinamide, More', 1767369485);
