INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184389, 'Amoxapine', 'Fostemsavir', 'Moderate', 'Fostemsavir can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval. Consider obtaining electrocardiogram and serum electrolyte levels prior to initiating fostemsavir therapy and periodically thereafter, particularly in patients with risk factors for torsade de pointes and/or receiving one or more potent CYP450 3A4 inhibitors (e.g., azole antifungal agents; cobicistat; protease inhibitors; ketolide and certain macrolide antibiotics; ceritinib; idelalisib; nefazodone) or P-gp inhibitors (e.g., cyclosporine; diltiazem; quinidine; quinine; tacrolimus; verapamil). Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184606/', '', 'Phenelzine, Duloxetine, Tranylcypromine, Vilazodone, Paroxetine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (184390, 'Amprenavir', 'Fostemsavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4, P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of temsavir, the active moiety of fostemsavir. Increased temsavir exposure may increase the risk of QT prolongation.', 'DDInter', 'No dosage adjustments are recommended for fostemsavir when used with potent CYP450 3A4, P-gp, and/or BCRP inhibitors. It may be advisable to monitor patients for increased adverse effects such as QT prolongation during coadministration, particularly in the elderly and patients with risk factors for torsade de pointes.', 'Metabolism, Absorption', 'No dosage adjustments are recommended for fostemsavir when used with potent CYP450 3A4, P-gp, and/or BCRP inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/184607/', '', 'Lamivudine, Cabotegravir, Maraviroc, Zanamivir, Valaciclovir, Lenacapavir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Stavudine, More', 'Glecaprevir, Cabotegravir, Amprenavir, Zanamivir, Valaciclovir, Lenacapavir, Velpatasvir, Rilpivirine, Oseltamivir, Brincidofovir, Remdesivir, More', 1767369485);
