INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (185698, 'Glyburide', 'Calcium carbonate', 'Moderate', 'INTERVAL: Some antacids have been reported to increase the rate of absorption and, rarely, the extent of absorption of oral sulfonylureas. The hypoglycemic effects of these agents may be altered.', 'DDInter', 'Sulfonylureas should be administered at least two hours before or after antacids when possible. Regular monitoring of blood sugar is advisable. In addition, patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their physician if it occurs.', 'Absorption', 'Sulfonylureas should be administered at least two hours before or after antacids when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185915/', '', 'Guar gum, Pramlintide, Alogliptin, Empagliflozin, Dulaglutide, Exenatide, Miglitol, Albiglutide, Rosiglitazone, Ertugliflozin, Semaglutide, More', 'Calcium glucoheptonate, Calcium lactate, Calcium chloride, Calcium gluconate, Calcium glubionate anhydrous, Calcium citrate, Calcium Phosphate, Ascorbic acid, Calcium chloride, Calcium citrate, Calcium acetate, Calcium Phosphate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (185699, 'Glyburide', 'Carteolol', 'Moderate', 'Beta-blockers may inhibit some of the normal physiologic response to hypoglycemia. Symptoms of hypoglycemia such as tremor and tachycardia may be absent, making it more difficult for patients to recognize an oncoming episode. Specifically, inhibition of catecholamine-mediated glycogenolysis and glucose mobilization in association with beta-blockade can potentiate insulin-induced hypoglycemia in diabetics and delay the recovery of normal blood glucose levels. Prolonged and severe hypoglycemia may occur, although these events have rarely been reported. Significant increases in blood pressure and bradycardia can also occur during hypoglycemia in diabetics treated with insulin and beta-blockers due to antagonism of epinephrine''s effect on beta-2 adrenergic receptors, which leads to unopposed alpha-adrenergic effects including vasoconstriction. Other effects reported with various beta-blockers include decreased glucose tolerance and decreased glucose-induced insulin secretion.', 'DDInter', 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients. Patients should be advised of the need for regular blood glucose monitoring and be aware that certain symptoms of hypoglycemia such as tremor and tachycardia may be masked. However, other symptoms such as headache, dizziness, drowsiness, confusion, nausea, hunger, weakness, and perspiration may be unaffected.', 'Others', 'In general, cardioselective beta-blockers are considered safer than noncardioselective agents in the treatment of diabetic patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185916/', '', 'Apraclonidine, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Brinzolamide, Levobunolol', 'Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Dulaglutide, Exenatide, Miglitol, Albiglutide, Rosiglitazone, Semaglutide, More', 1767369485);
