INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (185759, 'Glyburide', 'Methylcellulose', 'Minor', 'Large doses of soluble dietary fibers such as guar gum, psyllium, or methylcellulose may add to the hypoglycemic effect of insulin and oral antidiabetic agents. Soluble fibers slow the rate of carbohydrate degradation and glucose absorption during digestion, thus they can reduce postprandial blood glucose when taken in large amounts. Soluble dietary fibers are often considered beneficial as an adjunct to insulin and oral antidiabetic agents in the management of diabetes. However, patients should be monitored for changes in diabetic medication requirements following initiation of fiber therapy.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185976/', '', 'Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Empagliflozin, Dulaglutide, Exenatide, Albiglutide, Rosiglitazone, Ertugliflozin, More', 'Alvimopan, Sorbitol, Naloxegol, Bisacodyl, Naldemedine, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Prucalopride, Sodium sulfate, Linaclotide, More', 1767369485);
