INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (185953, 'Dapsone', 'Nitroglycerin', 'Moderate', 'Coadministration of oral or topical dapsone with other agents associated with methemoglobinemia may have additive effects and theoretically increase the risk of drug-induced methemoglobinemia. Oral and topical dapsone have been associated with methemoglobinemia, and the risk is increased in patients with glucose-6-phosphate dehydrogenase (G6PD) deficiency or congenital or idiopathic methemoglobinemia.', 'DDInter', 'Concurrent use of oral or topical dapsone with other methemoglobinemia-inducing medications should be followed with monitoring for signs and symptoms of methemoglobinemia (e.g., cyanosis, hypoxia).', 'Synergism', 'Concurrent use of oral or topical dapsone with other methemoglobinemia-inducing medications should be followed with monitoring for signs and symptoms of methemoglobinemia (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/186170/', '', 'Vitamin A, Isotretinoin, Chloramphenicol, Fluorometholone, Azelaic acid, Adapalene, Clofazimine', 'Nesiritide, Vericiguat, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Zinc chloride, Prednisolone, Betamethasone, Fluorometholone, Zinc gluconate, Dexamethasone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (185954, 'Cinchocaine (topical)', 'Nitroglycerin', 'Moderate', 'Some topical anesthetics can be systemically absorbed and cause methemoglobinemia, particularly when applied to mucous membranes. Coadministration with other oxidizing agents that can also induce methemoglobinemia such as injectable local anesthetics, antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk. Additional risk factors include very young age (e.g., infants less than 6 months); application to inflamed/abraded areas or broken skin; anemia; cardiac or pulmonary disease; peripheral vascular disease; liver cirrhosis; shock; sepsis; acidosis; and genetic predisposition (e.g., NADH cytochrome-b5 reductase deficiency; glucose-6-phosphate dehydrogenase (G6PD) deficiency; hemoglobin M).', 'DDInter', 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents. Clinicians should be aware of the potential for methemoglobinemia, particularly when topical anesthetics are applied to mucous membranes or given via the oropharyngeal route. Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia such as slate-grey cyanosis in buccal mucous membranes, lips, and nail beds; nausea; headache; dizziness; lightheadedness; lethargy; fatigue; dyspnea; tachypnea; tachycardia; palpitation; anxiety; and confusion.', 'Synergism', 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/186171/', '', 'Nesiritide, Vericiguat, Zinc sulfate, Diltiazem, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Zinc chloride, Prednisolone, Betamethasone, More', '', 1767369485);
