INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (187064, 'Ibritumomab tiuxetan', 'Capsicum', 'Minor', 'Theoretically, capsicum may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Animal and limited human data suggest that capsicum may increase fibrinolytic activity, lower plasma fibrinogen levels, increase plasma antithrombin III levels, inhibit platelet aggregation, and prolong bleeding time. In patients who have used capsicum extensively prior to receiving anticoagulation, antiplatelet, or thrombolytic therapy, the potential for an interaction should be considered. Clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187288/', '', 'Iodide I-131', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (187065, 'Ibritumomab tiuxetan', 'Chamomile', 'Minor', 'Some herbs such as chamomile and fenugreek have coumarin constituents. Theoretically, ingesting large quantities of these herbs may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. However, their effects on the coagulation system have not been studied, and bleeding complications have not been reported in humans. Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used chamomile or fenugreek extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187289/', '', 'Iodide I-131', '', 1767369485);
