INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (187102, 'Ibritumomab tiuxetan', 'Tositumomab', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Tositumomab and iodine I 131 tositumomab alone can cause severe or life-threatening thrombocytopenia.', 'DDInter', 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously. Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.', 'Synergism', 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187326/', '', 'Tositumomab, Iodide I-131', 'Ibritumomab tiuxetan, Iodide I-131', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (187103, 'Ibritumomab tiuxetan', 'Trichophyton mentagrophytes', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187327/', '', 'Iodide I-131', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (187104, 'Ibritumomab tiuxetan', 'Typhoid vaccine (inactivated)', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187328/', '', 'Iodide I-131', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (187105, 'Ibritumomab tiuxetan', 'Typhoid vaccine (live)', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187329/', '', 'Iodide I-131', '', 1767369485);
