INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (187198, 'Ibutilide', 'Glycerin', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187422/', '', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', 'Alvimopan, Sorbitol, Magnesium sulfate, Methylnaltrexone, Tegaserod, Magnesium oxide, Naloxegol, Plecanatide, Lubiprostone, Methylcellulose, Mannitol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (187199, 'Ibutilide', 'Grepafloxacin', 'Major', 'Grepafloxacin can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of grepafloxacin with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187423/', '', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', 'Enoxacin, Nalidixic acid, Cinoxacin, Trovafloxacin, Delafloxacin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (187200, 'Ibutilide', 'Halofantrine', 'Major', 'Halofantrine can cause dose-related prolongation of the QT interval at recommended therapeutic doses. QTc interval prolongation and death have been reported during combination use of halofantrine and mefloquine. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Coadministration of halofantrine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187424/', '', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', 'Artesunate, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 1767369485);
