INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188087, 'Indapamide', 'Chlorpromazine', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', 'DDInter', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188311/', '', 'Pimavanserin', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Ciclopirox, Darunavir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188088, 'Indapamide', 'Cisapride', 'Major', 'Some diuretics may produce hypokalemia and hypomagnesemia which could potentiate the risk of cisapride-induced arrhythmias in patients receiving both drugs concomitantly. Prolonged QT interval, torsades de pointes, and death have been reported.', 'DDInter', 'Cisapride is contraindicated in patients with uncorrected hypokalemia and hypomagnesemia, such as those taking potassium-wasting diuretics.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188312/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Ramipril, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188089, 'Indapamide', 'Clidinium', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of thiazide diuretics. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188313/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Verapamil, Ramipril, Lisinopril, Perindopril, Benazepril, Quinapril, Trandolapril, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188090, 'Indapamide', 'Deserpidine', 'Moderate', 'The hypotensive effects of thiazide diuretics and alpha-adrenergic blockers may be additive. Postural hypotension may occur.', 'DDInter', 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy. Patients should be advised to take the alpha-blocker at bedtime and to notify their physician if they experience dizziness or syncope while awake.', 'Synergism', 'Hemodynamic responses should be monitored during coadministration, especially during the first few weeks of therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188314/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Ramipril, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, Trandolapril, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188091, 'Indapamide', 'Dicoumarol', 'Minor', 'Thiazide diuretics may reduce intravascular volume. Higher plasma levels of clotting factors and decreased anticoagulant effect may result; however the effects may not be clinically significant. Management consists of monitoring coagulation parameters during the initial phase of coadministration. Patients should be advised to promptly report any signs of blood clots (e.g. chest pain, shortness of breath, sudden loss of vision, or pain, redness or swelling in an extremity).', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188315/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, Argatroban, More', 'Felodipine, Enalapril, Amlodipine, Moexipril, Verapamil, Ramipril, Lisinopril, Perindopril, Bisoprolol, Quinapril, Trandolapril, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188092, 'Indapamide', 'Diflunisal', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states. The risk may be increased in patients on dietary sodium restriction. At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure. Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination. NSAIDs may also increase the risk of hyperkalemia associated with potassium-sparing diuretics.', 'DDInter', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure. If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Antagonism', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188316/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Clindamycin, More', 'Salicylic acid, Salsalate, Choline salicylate, Ziconotide, Methoxyflurane', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188093, 'Indapamide', 'Digitoxin', 'Moderate', 'Although diuretics and digitalis glycosides are frequently and appropriately used together, diuretic-induced hypokalemia and hypomagnesemia may predispose patients on digitalis to arrhythmias.', 'DDInter', 'Digoxin, potassium and magnesium levels should be followed closely. Hypokalemia and hypomagnesemia should be treated appropriately. Digitalis dose adjustments may be required. Patients should be advised to notify their physicians if they experience signs of possible digoxin toxicity or electrolyte disturbances, such as weakness, lethargy, muscle pains or cramps, nausea, anorexia, visual disturbances, or irregular heartbeats.', 'Synergism', 'Digoxin, potassium and magnesium levels should be followed closely.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188317/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Ramipril, Lisinopril, Perindopril, Benazepril, Quinapril, Trandolapril, Captopril, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188094, 'Indapamide', 'Dihydrotachysterol', 'Moderate', 'Coadministration of thiazide diuretics with high dosages of calcium and/or vitamin D has been associated with reports of hypercalcemia in some patients. Thiazide diuretics inhibit the renal excretion of calcium and may also enhance responsiveness of bone and renal tubule to parathyroid hormone, thus concurrent use of large amounts of calcium or vitamin D can lead to excessively high plasma levels of calcium. Patients who are particularly susceptible include those with hyperparathyroidism, those being treated for osteoporosis, and those receiving high dosages of vitamin D for hypoparathyroidism. Metabolic alkalosis and the milk-alkali syndrome have been reported during prolonged therapy with thiazide diuretics and calcium.', 'DDInter', 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider. Serum calcium should be monitored if thiazide diuretics are coadministered with high dosages of calcium and/or vitamin D. Patients should be advised to seek medical attention if they experience signs and symptoms of hypercalcemia such as dizziness, weakness, lethargy, headache, myalgia, anorexia, nausea, vomiting, and seizures.', 'Synergism', 'Patients receiving thiazide diuretic therapy should be cautioned against self-treatment with calcium and vitamin D supplements without first talking to their healthcare provider.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188318/', '', 'Felodipine, Enalapril, Amlodipine, Moexipril, Verapamil, Ramipril, Lisinopril, Perindopril, Bisoprolol, Benazepril, Quinapril, More', 'Vitamin A, Beta carotene', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188095, 'Indapamide', 'Dimenhydrinate', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188319/', '', 'Moexipril, Benazepril, Tinidazole, Ascorbic acid, Hydroflumethiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, More', 'Ketotifen, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Doxylamine, Meclizine, More', 1767369485);
