INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188241, 'Indomethacin', 'Gentamicin', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', 'DDInter', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188465/', '', 'Fluocinolone acetonide, Fludrocortisone, Betamethasone, Fluorometholone, Misoprostol, Chondroitin sulfate, Glucosamine, Fluocinolone acetonide, Cortisone, Betamethasone, Fluorometholone, More', 'Tetracycline, Ganciclovir, Rifamycin, Oxytetracycline, Polymyxin B, Famciclovir, Sulfamethizole, Trifluridine, Benzylpenicillin, Sulfisoxazole, Chlorhexidine, More', 1767369485);
