INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188263, 'Indomethacin', 'Phenylpropanolamine', 'Moderate', 'The concurrent administration of indomethacin and phenylpropanolamine was associated with severe hypertension in one reported case. The mechanism is unknown but may be related to inhibition of prostaglandin synthesis resulting in higher sensitivity to sympathomimetic-induced blood pressure increases. This interaction was not demonstrated in a study with normotensive subjects.', 'DDInter', 'Until more information is available, close monitoring of blood pressure is recommended.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188487/', '', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Diclofenac, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Rabeprazole, Ketoprofen, More', 'Pseudoephedrine, Phenylephrine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188264, 'Indomethacin', 'Polythiazide', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states. The risk may be increased in patients on dietary sodium restriction. At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure. Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination. NSAIDs may also increase the risk of hyperkalemia associated with potassium-sparing diuretics.', 'DDInter', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure. If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Antagonism', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188488/', '', 'Fluocinolone acetonide, Fluorometholone, Misoprostol, Chondroitin sulfate, Glucosamine, Fluocinolone acetonide, Salicylic acid, Fluorometholone, Tolazoline, Dimethyl sulfoxide, Alprostadil, More', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Ciclopirox, Vemurafenib, Darunavir, Ketoconazole, Tioconazole, More', 1767369485);
