INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188748, 'Aminophylline', 'Enoxacin', 'Major', 'Enoxacin has been shown to reduce theophylline clearance by up to 75%. The mechanism is inhibition of CYP450 1A2 hepatic metabolism. Theophylline serum levels increase 1.5 to 3.0 times when enoxacin is administered concurrently. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', 'DDInter', 'If possible, enoxacin therapy should be avoided in patients receiving theophylline. Levofloxacin, sparfloxacin, moxifloxacin, gatifloxacin, trovafloxacin, and lomefloxacin have been reported to cause minor or no changes in theophylline levels and may be considered as alternatives. If enoxacin is given concomitantly with theophylline, the theophylline dosage should be reduced approximately 50% or more, and theophylline serum levels should be closely monitored. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats to their physician.', 'Metabolism', 'If possible, enoxacin therapy should be avoided in patients receiving theophylline.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188972/', '', 'Gatifloxacin, Levofloxacin, Gemifloxacin, Lomefloxacin, Moxifloxacin, Trovafloxacin, Delafloxacin, Sparfloxacin', 'Omalizumab, Tezepelumab, Benralizumab, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Zafirlukast, Bromotheophylline', 1767369485);
