INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (188774, 'Aminophylline', 'Propylthiouracil', 'Moderate', 'Clearance of theophylline and related agents depends upon thyroid function. In the hyperthyroid state, clearance may be enhanced. In the hypothyroid state, clearance may be reduced. Theophylline clearance may decrease and the risk of toxicity may increase when hyperthyroid patients become euthyroid.', 'DDInter', 'The patient and theophylline levels should be closely monitored while achieving a euthyroid state and when an antithyroid agent is added, discontinued, or the dosage is changed. Theophylline dosage adjustments may be required. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms, or any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats.', 'Others', 'The patient and theophylline levels should be closely monitored while achieving a euthyroid state and when an antithyroid agent is added, discontinued, or the dosage is changed.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/188998/', '', 'Potassium perchlorate', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Roflumilast, Pentoxifylline, More', 1767369485);
