INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (189323, 'Efalizumab', 'Tositumomab (I-131)', 'Moderate', 'The use of efalizumab with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Efalizumab alone can cause serious infections requiring hospitalization, and the risk may theoretically increase when coadministered with other immunosuppressive therapy. Agents that may be significantly myelo- or immunosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids.', 'DDInter', 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated. Patients receiving other immunosuppressive agents should not be treated with efalizumab because of the possibility of excessive immunosuppression. The duration of the period following treatment with efalizumab before one should consider starting other immunosuppressive therapy has not been evaluated.', 'Synergism', 'The safety and efficacy of efalizumab in combination with other immunosuppressive agents or phototherapy have not been evaluated.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189550/', '', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (189324, 'Hemin', 'Tositumomab (I-131)', 'Moderate', 'Theoretically, hemin may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Hemin has exhibited mild, transient anticoagulant effects during clinical studies.', 'DDInter', 'The use of hemin with concurrent anticoagulant therapy should be avoided. Caution is advised if hemin is used in combination with other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'The use of hemin with concurrent anticoagulant therapy should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189551/', '', 'Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Human C1-esterase inhibitor, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', '', 1767369485);
