INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (189659, 'Alemtuzumab', 'Acetylsalicylic acid', 'Moderate', 'Coadministration of alemtuzumab with antiplatelet or anticoagulant drugs may increase the risk of bleeding. Use of alemtuzumab alone has been associated with autoimmune and hemolytic anemia, thrombocytopenia, prolonged myelosuppression, bone marrow aplasia, hemophilia, and pancytopenia. The mechanism for this interaction has not been clearly delineated, but is presumably due to additive antiplatelet/anticoagulant effects.', 'DDInter', 'Caution is recommended if alemtuzumab is used concomitantly with antiplatelet or anticoagulant drugs. Patients should be monitored for increased anticoagulant effects and bleeding complications. Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if alemtuzumab is used concomitantly with antiplatelet or anticoagulant drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189886/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Abatacept, Siponimod, Anakinra, More', 'Iloprost, Treprostinil, Defibrotide, Antithrombin III human, Protein C, Acenocoumarol, Selexipag, Epoprostenol, Drotrecogin alfa, Felodipine, Amlodipine, More', 1767369485);
