INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (190198, 'Dapsone', 'Primaquine', 'Moderate', 'Coadministration of oral or topical dapsone with other agents associated with methemoglobinemia may have additive effects and theoretically increase the risk of drug-induced methemoglobinemia. Oral and topical dapsone have been associated with methemoglobinemia, and the risk is increased in patients with glucose-6-phosphate dehydrogenase (G6PD) deficiency or congenital or idiopathic methemoglobinemia.', 'DDInter', 'Concurrent use of oral or topical dapsone with other methemoglobinemia-inducing medications should be followed with monitoring for signs and symptoms of methemoglobinemia (e.g., cyanosis, hypoxia).', 'Synergism', 'Concurrent use of oral or topical dapsone with other methemoglobinemia-inducing medications should be followed with monitoring for signs and symptoms of methemoglobinemia (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190425/', '', 'Minocycline, Clindamycin, Vitamin A, Isotretinoin, Dexamethasone, Chloramphenicol, Fluorometholone, Tretinoin, Methylprednisolone, Azelaic acid, Adapalene', 'Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Halofantrine, Proguanil', 1767369485);
