INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191079, 'Gatifloxacin', 'Troglitazone', 'Major', 'Gatifloxacin may interfere with the therapeutic effects of insulin and other antidiabetic agents. The use of various quinolones has been associated with disturbances in blood glucose homeostasis possibly stemming from effects on pancreatic beta cell ATP-sensitive potassium channels that regulate insulin secretion.', 'DDInter', 'The use of gatifloxacin is contraindicated in patients with diabetes mellitus. Other quinolones may be safer alternatives in such patients, although all quinolones should be used with caution. Blood glucose should be closely monitored whenever quinolones are prescribed to patients receiving insulin or other antidiabetic agents, especially if they are elderly or have renal impairment. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during quinolone therapy, patients should initiate appropriate remedial therapy immediately, discontinue the antibiotic, and contact their physician.', 'Antagonism', 'The use of gatifloxacin is contraindicated in patients with diabetes mellitus.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191307/', '', 'Guar gum', 'Rifamycin, Kanamycin, Lomefloxacin, Ampicillin, Azithromycin, Acyclovir, Netilmicin, Polymyxin B, Famciclovir, Cefuroxime, Trifluridine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191080, 'Gatifloxacin', 'Typhoid vaccine (live)', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', 'DDInter', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191308/', '', 'Ganciclovir, Acyclovir, Famciclovir, Chlorhexidine, Povidone-iodine', '', 1767369485);
