INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191326, 'Heparin', 'Dexfenfluramine', 'Moderate', 'Serotonin reuptake inhibitors may potentiate the risk of bleeding in patients receiving heparin therapy. The exact mechanism is unknown but may involve a pharmacodynamic interaction, as serotonin reuptake inhibitors have been reported to interfere with platelet function.', 'DDInter', 'Caution is recommended if a serotonin reuptake inhibitor is administered with heparin therapy, especially in the elderly and patients with renal impairment. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if a serotonin reuptake inhibitor is administered with heparin therapy, especially in the elderly and patients with renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191554/', '', 'Dicoumarol, Protein C, Acenocoumarol, Selexipag, Anistreplase, Pentosan polysulfate, Ascorbic acid, Cyclosporine, Sirolimus, Potassium Iodide, Vitamin A, More', 'Naltrexone, Ephedrine, Phentermine, Lorcaserin, Diethylpropion, Mazindol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191327, 'Heparin', 'Dicoumarol', 'Major', 'Although therapy with heparin and oral anticoagulants often overlap in clinical practice, the potential for additive anticoagulant effects and increased risk of severe bleeding should be considered. Oral anticoagulants may prolong the activated partial thromboplastin time (aPTT) in patients receiving heparin, while heparin may prolong the International Normalized Ratio (INR) in patients receiving warfarin.', 'DDInter', 'During concurrent or overlapping therapy, close clinical and laboratory observation for hematologic complications is recommended. To minimize the interference of heparin on INR determinations, blood should not be drawn until at least 5 hours after the last intravenous heparin dose or 24 hours after the last subcutaneous heparin dose.', 'Synergism', 'During concurrent or overlapping therapy, close clinical and laboratory observation for hematologic complications is recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191555/', '', 'Selexipag, Heparin', 'Warfarin, Cilostazol, Dicoumarol, Antithrombin III human, Acenocoumarol, Selexipag, Anistreplase, Sirolimus, Potassium Iodide, Vitamin A, Acetylcysteine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191328, 'Heparin', 'Drotrecogin alfa', 'Major', 'The concurrent administration of therapeutic doses of heparin (greater than 15 units/kg/hour) and drotrecogin alfa may increase the risk of serious or life-threatening bleeding due to additive or synergistic effects on the clotting cascade. Drotrecogin alfa inactivates blood clotting Factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT). Most severely ill septic patients are already at a high risk of bleeding due to coagulopathies with prolonged APTT and prothrombin times (PT).', 'DDInter', 'When making the decision to administer drotrecogin alfa, the increased risk of bleeding versus the benefits of therapy should be carefully considered in seriously ill sepsis patients who are concurrently receiving therapeutic heparin (greater than 15 units/kg/hour). Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In clinical trials, prophylactic low doses of heparin did not appear to affect safety; however, the efficacy of concurrent drotrecogin alfa and heparin in treating sepsis patients has not been determined.', 'Synergism', 'When making the decision to administer drotrecogin alfa, the increased risk of bleeding versus the benefits of therapy should be carefully considered in seriously ill sepsis patients who are concurrently receiving therapeutic heparin (greater than 15 units/kg/hour).', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191556/', '', 'Selexipag, Heparin', 'Acenocoumarol, Selexipag, Drotrecogin alfa, Ascorbic acid, Cyclosporine, Sirolimus, Potassium Iodide, Vitamin A, Acetylcysteine, Nandrolone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191329, 'Heparin', 'Hemin', 'Moderate', 'Theoretically, hemin may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Hemin has exhibited mild, transient anticoagulant effects during clinical studies.', 'DDInter', 'The use of hemin with concurrent anticoagulant therapy should be avoided. Caution is advised if hemin is used in combination with other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'The use of hemin with concurrent anticoagulant therapy should be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191557/', '', 'Protein C, Acenocoumarol, Selexipag, Desirudin, Ascorbic acid, Cyclosporine, Sirolimus, Potassium Iodide, Vitamin A, Acetylcysteine, Nandrolone', 'Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Human C1-esterase inhibitor, Berotralstat, Lanadelumab, Icatibant, Crizanlizumab', 1767369485);
