INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191353, 'Hepatitis B Vaccine (Recombinant)', 'Melphalan flufenamide', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191581/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191354, 'Zalcitabine', 'Aurothioglucose', 'Moderate', 'Zalcitabine can cause peripheral neuropathy in up to one-third of patients with advanced HIV disease, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Zalcitabine-related peripheral neuropathy is a sensorimotor neuropathy characterized initially by numbness and burning dysesthesia involving the distal extremities. These symptoms may be followed by sharp shooting pains or severe continuous burning pain if the drug is not withdrawn, and progress to severe pain requiring narcotic analgesics. The neuropathy is potentially irreversible. However, with prompt discontinuation of zalcitabine, it is usually slowly reversible, although symptoms may initially progress following discontinuation.', 'DDInter', 'Use of zalcitabine with other drugs that have the potential to cause peripheral neuropathy should be avoided whenever possible. Otherwise, careful monitoring is recommended for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness in the extremities, particularly in patients with a low CD4 cell count or diabetes. Since the development of peripheral neuropathy appears to be dose-related, the recommended dosage of zalcitabine should not be exceeded. Patients should be advised to promptly discontinue zalcitabine therapy and contact their physician if neuropathy develops. Therapy may be reinstituted following resolution of neuropathy symptoms, but dosage should be reduced to one-half the initially recommended dosage. Zalcitabine should be permanently discontinued in patients who develop severe peripheral neuropathy during treatment.', 'Synergism', 'Use of zalcitabine with other drugs that have the potential to cause peripheral neuropathy should be avoided whenever possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191582/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Penicillamine', 1767369485);
