INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191650, 'Anisindione', 'Interferon alfa-n3', 'Moderate', 'Interferons may increase the plasma concentrations and pharmacologic effects of warfarin and other oral anticoagulants. At least one case of this interaction has been reported in the literature. The mechanism is unknown but may be related to interferon-induced suppression of hepatic CYP450 enzymes.', 'DDInter', 'Caution is advised if oral anticoagulants are administered with an interferon. The INR should be monitored closely and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of interferon therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Others', 'Caution is advised if oral anticoagulants are administered with an interferon.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/191878/', '', '', '', 1767369485);
