INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (191954, 'Digitoxin', 'Licorice', 'Moderate', 'Chronic use of licorice may potentiate the risk of digitalis toxicity and other cardiovascular complications in patients treated with cardiac glycosides. Glycyrrhizic acid, a component of licorice, can induce hypokalemia, hypernatremia, edema, hypertension, and suppression of the renin-aldosterone system by virtue of its mineralocorticoid activity. In digitalized patients, electrolyte imbalance such as hypokalemia and hypomagnesemia can lead to arrhythmias, while other chronic effects of glycyrrhizic acid can compromise overall cardiovascular condition.', 'DDInter', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. In general, chronic use of licorice and licorice-containing products should be avoided in patients treated with cardiac glycosides.', 'Synergism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192182/', '', '', '', 1767369485);
