INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (192600, 'Estrone sulfate (topical)', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. In vitro and in vivo studies have shown that both estrogens and progestins are partially metabolized by CYP450 3A4. Studies of letermovir with midazolam, a CYP450 3A4 probe substrate, have shown the net effect of letermovir on the CYP450 3A4 isoenzyme is moderate inhibition.', 'DDInter', 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy. When choosing an oral contraceptive, consider choosing an ethinyl estradiol-levonorgestrel product as this combination has been studied with letermovir and no clinically significant interaction has been found.', 'Metabolism', 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/192828/', '', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Velpatasvir, Rilpivirine, More', '', 1767369485);
