INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (192937, 'Deserpidine', 'Phenylephrine (nasal)', 'Moderate', 'Sympathomimetic amines may decrease the hypotensive effect of postganglionic adrenergic blocking agents, while the latter may potentiate the pharmacologic effects of direct-acting sympathomimetic amines (e.g., dobutamine, epinephrine, methoxamine, norepinephrine) but inhibit those that are primarily indirect-acting (e.g., mephentermine). Postganglionic adrenergic blocking agents such as guanadrel, guanethidine, and rauwolfia alkaloids work by depleting catecholamine stores from adrenergic nerve endings. Therefore, they may sensitize adrenergic receptors to direct-acting sympathomimetics, but blunt the effects of indirect-acting agents whose activity is mediated through the release of catecholamines.', 'DDInter', 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension. Alternatives to postganglionic adrenergic blocking agents should be considered if patients are treated with sympathomimetic amines, since effects of the latter may be intensified or diminished depending on whether they are direct- or indirect-acting. Most agents with indirect sympathomimetic activity are mixed-acting, thus it may be difficult to predict how they will be affected by postganglionic adrenergic blocking agents. If the combination is used, blood pressure and heart rate should be monitored.', 'Antagonism', 'Due to their pressor effect, sympathomimetic amines should be used cautiously in patients with hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193165/', '', 'Prazosin, Clonidine, Hydralazine, Guanfacine, Clonidine, Methyldopa', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (192938, 'Ergometrine', 'Phenylephrine (nasal)', 'Major', 'Additive or synergistic increases in blood pressure and/or ischemic response may occur when ergot alkaloids are combined with peripheral or central vasoconstrictors. Ergot alkaloids produce arterial vasoconstriction by stimulating alpha-adrenergic and serotonin receptors and inhibiting endothelial-derived relaxation factor release.', 'DDInter', 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents. If concomitant use is required, patients should be monitored for excessive vasoconstriction and have vital signs measured regularly. Patients should also be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Ergot alkaloids should generally not be administered in combination with other vasoconstrictive agents.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193166/', '', 'Misoprostol, Dinoprostone, Oxytocin', '', 1767369485);
