INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (193098, 'Quinidine', 'Magaldrate', 'Moderate', 'Coadministration with drugs that can increase urinary pH such as antacids, carbonic anhydrase inhibitors, or urinary alkalinizers may decrease the urinary excretion of quinidine. The proposed mechanism is increased renal tubular reabsorption due to reduced ionization of quinidine in alkaline urine.', 'DDInter', 'Caution is advised if quinidine is used with agents that can increase urinary pH. Quinidine levels may need to be monitored more closely following addition or discontinuation of these agents, and the quinidine dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate quinidine toxicity such as tinnitus, hearing loss, visual disturbances, diarrhea, headache, dizziness, palpitations, syncope, or irregular heartbeats.', 'Excretion', 'Caution is advised if quinidine is used with agents that can increase urinary pH.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193326/', '', 'Amiodarone, Dronedarone, Lidocaine, Propafenone, Procainamide, Tocainide, Flecainide, Disopyramide, Mexiletine, Dofetilide, Bretylium, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (193099, 'Quinidine', 'Mesoridazine', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193327/', '', 'Lidocaine, Tocainide, Mexiletine, Bretylium, Moricizine', 'Thiothixene, Lumateperone, Loxapine, Molindone, Cariprazine, Lurasidone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (193100, 'Quinidine', 'Methohexital', 'Moderate', 'Some barbiturates may significantly decrease serum quinidine concentrations and half-life. The mechanism is induction of CYP450 3A4 hepatic metabolism of quinidine. Data are available for pentobarbital, phenobarbital, and primidone.', 'DDInter', 'Monitor the effectiveness and serum levels of quinidine until a stable, safe, and therapeutic level is achieved after a barbiturate is added or deleted from this patient''s regimen. Dose adjustments may be required. Patients should be advised to notify their physician if they experience worsening irregular heartbeats or exacerbation of other symptoms. Patients should also be observed for quinidine toxicity after the barbiturate is discontinued.', 'Metabolism', 'Monitor the effectiveness and serum levels of quinidine until a stable, safe, and therapeutic level is achieved after a barbiturate is added or deleted from this patient''s regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/193328/', '', 'Suvorexant, Flurazepam, Zaleplon, Dichloralphenazone, Valerian, Chloral hydrate, Quazepam, Paraldehyde, Ramelteon, Lemborexant, Ethchlorvynol, More', 'Lidocaine, Dronedarone, Tocainide, Procainamide, Flecainide, Mexiletine, Dofetilide, Bretylium, Moricizine, Ibutilide', 1767369485);
